import React from 'react';
import './App.css';

const arr = [1, 4, 6, 8, 45, 89];

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      arr: arr,
      filternumber: ''
    }
  }

  getUserNumber(event) {
    let value = event.currentTarget.value;
    if (value !== '') {
      value = parseInt(value);
    }
    //o es cadena vacía o es un número

    this.setState({
      filternumber: value
    });

    console.log(value);
  }

  render() {
    return (
      <div className="App">
        <input type="number" onChange={this.getUserNumber}/>
        <ul className="list">
          {this.state.arr
            .filter(item => {
              if (this.state.filternumber === ''){
                //todos los elementos
                return true;
              } else {
                return this.state.filternumber <= item;
              }
            })
            .map(number => {
            return (
              <li key={number}>{number}</li>
            );
          })}
        </ul>

      </div>
    );
  }
}

export default App;
